
public class DecisionMakingWithOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=7, y=8;
		int a=10;
		int b=20;
		if(x>y) {
			System.out.println("True");
		}
		else {
			System.out.println("False");
		}
		
		if(!(a<b) || (a==b)) {
			System.out.println("True");
		}
		else {
			System.out.println("False");
		}
	}

}
