
public class DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=3;
		do {
			System.out.println(i);
			i++;
		}while(i<=11);
	
	System.out.println("------------------------");
	
	
		int j=4;
		do {
			System.out.println(j);
			j++;
		}
		while(j<=10);
	}

}
