
public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=10;i++) {
			System.out.println("value of i");
			System.out.println(i);
		}
		
		int j;
		for(j=1;j<=10;j++)
			System.out.println("the value of j");
		    System.out.println(j);

	}

}
