
public class ForLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		for(i=1;i<=10;i++) 
			System.out.println("the value of i");
			System.out.println(i);
		
			
	   int j;
	   for(j=1;j<=20;j++) {
		   System.out.println("the value of j");
		   System.out.println(j);
	   }

	}

}
